(8031, 155, 'page_builder_0_content', '<h1>Metaverse, <em>where Mythology</em> meets blockchain</h1>\r\nMultiverse of Legends is a web3 project where you can immerse into the magical universe of mythology, create and trade in-game assets, earn while playing and explore world intangible heritage.'),
(8032, 155, '_page_builder_0_content', 'field_6329bb6cd43c0'),
(8033, 155, 'page_builder_0_image', '61'),
(8034, 155, '_page_builder_0_image', 'field_6329bb78d43c1'),
(8035, 155, 'page_builder', 'a:10:{i:0;s:5:\"intro\";i:1;s:24:\"content_with_cover_image\";i:2;s:4:\"join\";i:3;s:9:\"evolution\";i:4;s:24:\"content_with_cover_image\";i:5;s:8:\"products\";i:6;s:7:\"roadmap\";i:7;s:5:\"about\";i:8;s:4:\"faqs\";i:9;s:10:\"contact_us\";}'),
(8036, 155, '_page_builder', 'field_6329bb5ad43bf'),
(8037, 155, 'page_builder_1_content', '<h2 style=\"text-align: center;\">Become a real <em>Legend!</em></h2>\r\n<p style=\"text-align: center;\">Magical Worlds of myths and legends have always been hidden from common people. Until now. The new Metaverse will give you an opportunity to reach the mythological lands, join Gods &amp; Heroes in their eternal war with Chaos and possibly become a Legend for the future generations!</p>'),
(8038, 155, '_page_builder_1_content', 'field_6329bbadd43c3'),
(8039, 155, 'page_builder_1_image', '131'),
(8040, 155, '_page_builder_1_image', 'field_6329bbbfd43c4'),
(8041, 155, 'page_builder_2_content', '<h2>Join <em>unique platform</em>, where mythological heroes, legends of the past, rituals and artifacts are <em>embodied in the virtual world.</em></h2>\r\nWe build a platform that will give you an opportunities to have fun, explore, create, earn and contribute to society in truly magical virtual environment… and beyond.'),
(8042, 155, '_page_builder_2_content', 'field_6329bc1dd43c6'),
(8043, 155, 'page_builder_2_items_0_title', 'Play'),
(8044, 155, '_page_builder_2_items_0_title', 'field_6329bc41d43c8'),
(8045, 155, 'page_builder_2_items_0_description', 'Fight with the monsters of Chaos in PvE battles or grow the power and skills of your Legend, playing exciting games and completing logical tasks and quests.'),
(8046, 155, '_page_builder_2_items_0_description', 'field_6329bc57d43c9'),
(8047, 155, 'page_builder_2_items_0_image', '76'),
(8048, 155, '_page_builder_2_items_0_image', 'field_6329bc67d43ca'),
(8049, 155, 'page_builder_2_items_1_title', 'Earn'),
(8050, 155, '_page_builder_2_items_1_title', 'field_6329bc41d43c8'),
(8051, 155, 'page_builder_2_items_1_description', 'Get the victory bonus for every successful fight, become a Leader of the Cult and receive rewards for each newcomer, trade your in-game NFT assets or earn passive income by staking them.'),
(8052, 155, '_page_builder_2_items_1_description', 'field_6329bc57d43c9'),
(8053, 155, 'page_builder_2_items_1_image', '77'),
(8054, 155, '_page_builder_2_items_1_image', 'field_6329bc67d43ca'),
