(8741, 6, 'page_builder_9_description', '<h3>Досліджуйте <em>світ</em> міфології</h3>\r\n<p class=\"checkViewport fadeIn\">Наші персонажі з неймовірними історіями та захоплюючими пригодами вже чекають</p>'),
(8742, 6, '_page_builder_9_description', 'field_6334222fd1563'),
(8743, 158, 'page_builder_0_content', '<h1>Metaverse, <em>where Mythology</em> meets blockchain</h1>\r\nMultiverse of Legends is a web3 project where you can immerse into the magical universe of mythology, create and trade in-game assets, earn while playing and explore world intangible heritage.'),
(8744, 158, '_page_builder_0_content', 'field_6329bb6cd43c0'),
(8745, 158, 'page_builder_0_image', '61'),
(8746, 158, '_page_builder_0_image', 'field_6329bb78d43c1'),
(8747, 158, 'page_builder', 'a:10:{i:0;s:5:\"intro\";i:1;s:24:\"content_with_cover_image\";i:2;s:4:\"join\";i:3;s:9:\"evolution\";i:4;s:24:\"content_with_cover_image\";i:5;s:8:\"products\";i:6;s:7:\"roadmap\";i:7;s:5:\"about\";i:8;s:4:\"faqs\";i:9;s:10:\"contact_us\";}'),
(8748, 158, '_page_builder', 'field_6329bb5ad43bf'),
(8749, 158, 'page_builder_1_content', '<h2 style=\"text-align: center;\">Become a real <em>Legend!</em></h2>\r\n<p style=\"text-align: center;\">Magical Worlds of myths and legends have always been hidden from common people. Until now. The new Metaverse will give you an opportunity to reach the mythological lands, join Gods &amp; Heroes in their eternal war with Chaos and possibly become a Legend for the future generations!</p>'),
(8750, 158, '_page_builder_1_content', 'field_6329bbadd43c3'),
(8751, 158, 'page_builder_1_image', '131'),
(8752, 158, '_page_builder_1_image', 'field_6329bbbfd43c4'),
(8753, 158, 'page_builder_2_content', '<h2>Join <em>unique platform</em>, where mythological heroes, legends of the past, rituals and artifacts are <em>embodied in the virtual world.</em></h2>\r\nWe build a platform that will give you an opportunities to have fun, explore, create, earn and contribute to society in truly magical virtual environment… and beyond.'),
(8754, 158, '_page_builder_2_content', 'field_6329bc1dd43c6'),
(8755, 158, 'page_builder_2_items_0_title', 'Play'),
(8756, 158, '_page_builder_2_items_0_title', 'field_6329bc41d43c8'),
(8757, 158, 'page_builder_2_items_0_description', 'Fight with the monsters of Chaos in PvE battles or grow the power and skills of your Legend, playing exciting games and completing logical tasks and quests.'),
(8758, 158, '_page_builder_2_items_0_description', 'field_6329bc57d43c9'),
(8759, 158, 'page_builder_2_items_0_image', '76'),
(8760, 158, '_page_builder_2_items_0_image', 'field_6329bc67d43ca'),
(8761, 158, 'page_builder_2_items_1_title', 'Earn'),
(8762, 158, '_page_builder_2_items_1_title', 'field_6329bc41d43c8'),
(8763, 158, 'page_builder_2_items_1_description', 'Get the victory bonus for every successful fight, become a Leader of the Cult and receive rewards for each newcomer, trade your in-game NFT assets or earn passive income by staking them.'),
(8764, 158, '_page_builder_2_items_1_description', 'field_6329bc57d43c9'),
(8765, 158, 'page_builder_2_items_1_image', '77'),
(8766, 158, '_page_builder_2_items_1_image', 'field_6329bc67d43ca'),
