(8768, 158, '_page_builder_2_items_2_title', 'field_6329bc41d43c8'),
(8769, 158, 'page_builder_2_items_2_description', 'Explore amazing locations connected to specific mythology and folklore. There are places where you may find and buy special NFT-artifacts, real merch or get some free perks, places where you can meet famous people or learn secret knowledge from the past and even those where you can play games of luck and win. '),
(8770, 158, '_page_builder_2_items_2_description', 'field_6329bc57d43c9'),
(8771, 158, 'page_builder_2_items_2_image', '78'),
(8772, 158, '_page_builder_2_items_2_image', 'field_6329bc67d43ca'),
(8773, 158, 'page_builder_2_items_3_title', 'Create'),
(8774, 158, '_page_builder_2_items_3_title', 'field_6329bc41d43c8'),
(8775, 158, 'page_builder_2_items_3_description', 'Contribute to the Metaverse growth and get the revenue! Create artworks, 3D-models, scenarios, game strategies, music and sounds or any other in-game assets and receive part of the platform\'s shared revenue or sell them as NFTs via the Marketplace platform. '),
