(9115, 163, 'page_builder', 'a:10:{i:0;s:5:\"intro\";i:1;s:24:\"content_with_cover_image\";i:2;s:4:\"join\";i:3;s:9:\"evolution\";i:4;s:24:\"content_with_cover_image\";i:5;s:8:\"products\";i:6;s:7:\"roadmap\";i:7;s:5:\"about\";i:8;s:4:\"faqs\";i:9;s:10:\"contact_us\";}'),
(9116, 163, '_page_builder', 'field_6329bb5ad43bf'),
(9117, 163, 'page_builder_1_content', '<h2 style=\"text-align: center;\">Become a real <em>Legend!</em></h2>\r\n<p style=\"text-align: center;\" class=\"checkViewport fadeIn\">Magical Worlds of myths and legends have always been hidden from common people. Until now. The new Metaverse will give you an opportunity to reach the mythological lands, join Gods &amp; Heroes in their eternal war with Chaos and possibly become a Legend for the future generations!</p>'),
(9118, 163, '_page_builder_1_content', 'field_6329bbadd43c3'),
(9119, 163, 'page_builder_1_image', '131'),
(9120, 163, '_page_builder_1_image', 'field_6329bbbfd43c4'),
(9121, 163, 'page_builder_2_content', '<h2>Join <em>unique platform</em>, where mythological heroes, legends of the past, rituals and artifacts are <em>embodied in the virtual world.</em></h2>\r\nWe build a platform that will give you an opportunities to have fun, explore, create, earn and contribute to society in truly magical virtual environment… and beyond.'),
(9122, 163, '_page_builder_2_content', 'field_6329bc1dd43c6'),
(9123, 163, 'page_builder_2_items_0_title', 'Play'),
(9124, 163, '_page_builder_2_items_0_title', 'field_6329bc41d43c8'),
(9125, 163, 'page_builder_2_items_0_description', 'Fight with the monsters of Chaos in PvE battles or grow the power and skills of your Legend, playing exciting games and completing logical tasks and quests.'),
(9126, 163, '_page_builder_2_items_0_description', 'field_6329bc57d43c9'),
(9127, 163, 'page_builder_2_items_0_image', '76'),
(9128, 163, '_page_builder_2_items_0_image', 'field_6329bc67d43ca'),
(9129, 163, 'page_builder_2_items_1_title', 'Earn'),
(9130, 163, '_page_builder_2_items_1_title', 'field_6329bc41d43c8'),
(9131, 163, 'page_builder_2_items_1_description', 'Get the victory bonus for every successful fight, become a Leader of the Cult and receive rewards for each newcomer, trade your in-game NFT assets or earn passive income by staking them.'),
(9132, 163, '_page_builder_2_items_1_description', 'field_6329bc57d43c9'),
(9133, 163, 'page_builder_2_items_1_image', '77'),
(9134, 163, '_page_builder_2_items_1_image', 'field_6329bc67d43ca'),
(9135, 163, 'page_builder_2_items_2_title', 'Explore '),
(9136, 163, '_page_builder_2_items_2_title', 'field_6329bc41d43c8'),
(9137, 163, 'page_builder_2_items_2_description', 'Explore amazing locations connected to specific mythology and folklore. There are places where you may find and buy special NFT-artifacts, real merch or get some free perks, places where you can meet famous people or learn secret knowledge from the past and even those where you can play games of luck and win. '),
(9138, 163, '_page_builder_2_items_2_description', 'field_6329bc57d43c9'),
(9139, 163, 'page_builder_2_items_2_image', '78'),
(9140, 163, '_page_builder_2_items_2_image', 'field_6329bc67d43ca'),
(9141, 163, 'page_builder_2_items_3_title', 'Create'),
(9142, 163, '_page_builder_2_items_3_title', 'field_6329bc41d43c8'),
(9143, 163, 'page_builder_2_items_3_description', 'Contribute to the Metaverse growth and get the revenue! Create artworks, 3D-models, scenarios, game strategies, music and sounds or any other in-game assets and receive part of the platform\'s shared revenue or sell them as NFTs via the Marketplace platform. '),
(9144, 163, '_page_builder_2_items_3_description', 'field_6329bc57d43c9'),
(9145, 163, 'page_builder_2_items_3_image', '79'),
(9146, 163, '_page_builder_2_items_3_image', 'field_6329bc67d43ca'),
(9147, 163, 'page_builder_2_items', '4'),
(9148, 163, '_page_builder_2_items', 'field_6329bc27d43c7'),
(9149, 163, 'page_builder_3_content', '<h2>Evolution <em>of the ambitious project</em></h2>\r\nFounded in Ukraine in 2020. Aimed to popularize mythology. Supported by Ukrainian Cultural Foundation.'),
(9150, 163, '_page_builder_3_content', 'field_6329bc8c968df'),
(9151, 163, 'page_builder_3_items_0_title', 'Mobile application <br/><em>with AR</em>'),
(9152, 163, '_page_builder_3_items_0_title', 'field_6329bcb0968e1'),
(9153, 163, 'page_builder_3_items_0_description', 'A bright, interesting and educational application with 20 characters from Ukrainian myths is an ideal option for \"smart\" entertainment.'),
(9154, 163, '_page_builder_3_items_0_description', 'field_6329bcb6968e2'),
(9155, 163, 'page_builder_3_items_0_image', '81'),
(9156, 163, '_page_builder_3_items_0_image', 'field_6329bcc4968e3'),
(9157, 163, 'page_builder_3_items_1_title', 'Educational <br/><em>products</em>'),
(9158, 163, '_page_builder_3_items_1_title', 'field_6329bcb0968e1'),
(9159, 163, 'page_builder_3_items_1_description', 'Studying Ukrainian mythology is more interesting than ever before. Have fun, learn and meet mythical heroes in augmented reality.'),
(9160, 163, '_page_builder_3_items_1_description', 'field_6329bcb6968e2'),
(9161, 163, 'page_builder_3_items_1_image', '82'),
(9162, 163, '_page_builder_3_items_1_image', 'field_6329bcc4968e3'),
(9163, 163, 'page_builder_3_items_2_title', 'Tabletop <br/><em>game</em>'),
(9164, 163, '_page_builder_3_items_2_title', 'field_6329bcb0968e1'),
(9165, 163, 'page_builder_3_items_2_description', 'The game will help you get to know the heroes of Ukrainian mythology and have fun discovering their \"superpowers\".'),
(9166, 163, '_page_builder_3_items_2_description', 'field_6329bcb6968e2'),
(9167, 163, 'page_builder_3_items_2_image', '83'),
