(9169, 163, 'page_builder_3_items_3_title', 'Animated series <br/><em>(Pre-production)</em>'),
(9170, 163, '_page_builder_3_items_3_title', 'field_6329bcb0968e1'),
(9171, 163, 'page_builder_3_items_3_description', '\"Magical world\" is an animated series for children based on Ukrainian mythology, which takes place in a fantastic universe.'),
(9172, 163, '_page_builder_3_items_3_description', 'field_6329bcb6968e2'),
(9173, 163, 'page_builder_3_items_3_image', '84'),
