(10426, 197, '_page_builder_2_items_0_title', 'field_6329bc41d43c8'),
(10427, 197, 'page_builder_2_items_0_description', 'Fight with the monsters of Chaos in PvE battles or grow the power and skills of your Legend, playing exciting games and completing logical tasks and quests.'),
