(10806, 206, '_page_builder_2_items_1_title', 'field_6329bc41d43c8'),
(10807, 206, 'page_builder_2_items_1_description', 'Get the victory bonus for every successful fight, become a Leader of the Cult and receive rewards for each newcomer, trade your in-game NFT assets or earn passive income by staking them.'),
(10808, 206, '_page_builder_2_items_1_description', 'field_6329bc57d43c9'),
(10809, 206, 'page_builder_2_items_1_image', '175'),
