(10812, 206, '_page_builder_2_items_2_title', 'field_6329bc41d43c8'),
(10813, 206, 'page_builder_2_items_2_description', 'Explore amazing locations connected to specific mythology and folklore. There are places where you may find and buy special NFT-artifacts, real merch or get some free perks, places where you can meet famous people or learn secret knowledge from the past and even those where you can play games of luck and win. '),
(10814, 206, '_page_builder_2_items_2_description', 'field_6329bc57d43c9'),
(10815, 206, 'page_builder_2_items_2_image', '176'),
(10816, 206, '_page_builder_2_items_2_image', 'field_6329bc67d43ca'),
(10817, 206, 'page_builder_2_items_3_title', 'Create'),
