(11541, 220, '_page_builder_7_items_0_title', 'field_6329bf58f2852'),
(11542, 220, 'page_builder_7_items_0_description', 'To preserve intangible cultural heritage of the humanity by transferring it to the virtual Metaverse.'),
(11543, 220, '_page_builder_7_items_0_description', 'field_6329bf60f2853'),
