(11589, 220, '_page_builder_3_items_1_title', 'field_6329bcb0968e1'),
(11590, 220, 'page_builder_3_items_1_description', 'Studying Ukrainian mythology is more interesting than ever before. Have fun, learn and meet mythical heroes in augmented reality.'),
